import React from "react";
import Typed from 'typed.js';
import "./Home.css";


function Home() {
    const el = React.useRef(null);

    React.useEffect(() => {
        const typed = new Typed(el.current, {
            strings: [
                        "‏‏‎ ‎Developer.", 
                        "‏‏‎ ‎Student.", 
                        "n Enthusiast.", 
                        "‏‏‎ ‎Professional Coder."
                    ],
            typeSpeed: 30,
            backSpeed: 50,
            loop: true,
        });

        return () => {
            typed.destroy();
        };
    }, []);

    return (
        <>
            <div id="home">
                <div id="home-text">
                    <h1>Hi, I'm <span id="name">Prajwal Shrimali</span></h1>
                    <h2>a<span ref={el} /></h2>
                    <br />
                    <p className="info-text">
                        I'm a code-cracking, mystery-solving dynamo! As a developer, student, and all-around enthusiast, 
                        I'm on a thrilling quest to unravel the secrets of our digital universe. Join me in this 
                        adrenaline-fueled adventure as we unlock the wonders of technology and embrace the excitement 
                        of discovery!
                    </p>
                </div>
                <div id="home-image">
                    <div className="background"></div>
                    <img src="https://via.placeholder.com/600" alt="Prajwal Shrimali Display Pic" />
                </div>
            </div>
            <hr />
        </>
        
    );
}

export default Home;
