import React, { useEffect, useState } from "react";
import "./Navbar.css";
import Image from "./../../asset/myImage-removebg.png";

function Navbar() {
    const [scroll, setScroll] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            const scrollTop = window.scrollY;
            if (scrollTop > 150) {
                setScroll(true);
            } else {
                setScroll(false);
            }
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, []);

    return (
        <div className={`navbarContainer ${scroll ? "scrolled" : ""}`}>
            <nav>
                <div className={`logoContainer ${scroll ? "scrolled" : ""}`}>
                    <img src={Image} alt="Prajwal Shrimali" className="circularImage"/>
                    <h1>Prajwal Shrimali</h1>
                </div>
                <ul className={`navbar ${scroll ? "scrolled" : ""}`}>
                    <li><a href="#home">Home</a></li>
                    <li><a href="#about">About</a></li>
                    <li><a href="#education">Education</a></li>
                    <li><a href="#experience">Experience</a></li> {/* Fixed typo here */}
                    <li><a href="#projects">Projects</a></li>
                    <li><a href="#contact">Contact</a></li>
                </ul>
            </nav>
        </div>
    );
}

export default Navbar;
